import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function NavBar() {
  const [lang, setLang] = useState('EN');

  const toggleLanguage = () => {
    setLang(lang === 'EN' ? 'VN' : 'EN');
  };

  return (
    <nav style={{ padding: '10px', borderBottom: '1px solid #ccc' }}>
      <Link style={{ marginRight: '10px' }} to="/">Homepage</Link>
      <Link style={{ marginRight: '10px' }} to="/about">About Us</Link>
      <Link style={{ marginRight: '10px' }} to="/products">Products Center</Link>
      <Link style={{ marginRight: '10px' }} to="/news">News & Activities</Link>
      <Link style={{ marginRight: '10px' }} to="/social">Social Responsibility</Link>
      <Link style={{ marginRight: '10px' }} to="/contact">Contact Us</Link>
      <Link style={{ marginRight: '10px' }} to="/join">Join Us</Link>
      <button onClick={toggleLanguage} style={{ marginLeft: '20px' }}>
         {lang === 'EN' ? 'Switch to VN' : 'Chuyển sang EN'}
      </button>
    </nav>
  );
}

export default NavBar;
