import React, { useEffect, useState } from 'react';
import '../App.css';

function Homepage() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch('http://localhost:8000/api/products/')
      .then(response => response.json())
      .then(data => setProducts(data))
      .catch(error => console.error('Error fetching products:', error));
  }, []);

  return (
    <div>
      {/* Hero Banner */}
      <div className="hero-banner">
        <h2>Welcome to Shundao</h2>
      </div>
      <div className="container">
        {/* Brand Advantage */}
        <div className="section">
          <h2>Brand Advantage</h2>
          <ul>
            <li>Along the way of ten years of trees, you can see lush ecology everywhere, With world-class equipment, talents, and skilled technicians, we hope to become your first choice.</li>
          </ul>
        </div>
        {/* Main Products */}
        <div className="section">
          <h2>Main Products</h2>
          <ul>
            {products.map(product => (
              <li key={product.id} style={{ marginBottom: '20px' }}>
                <h3>{product.name}</h3>
                <p>{product.description}</p>
                {product.image && <img src={`http://localhost:8000${product.image}`} alt={product.name} style={{ maxWidth: '100%', borderRadius: '8px' }} />}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default Homepage;
