import React from 'react';
import Slider from "react-slick";
import "../App.css";
// 请在 src/assets/images/ 下放置轮播所需的图片，并确保文件名一致
import banner1 from "../assets/images/banner1.jpg";
import banner2 from "../assets/images/banner2.png";
import banner3 from "../assets/images/banner3.png";

function Home() {
  // 轮播设置
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    autoplay: true,
    autoplaySpeed: 3000,
    slidesToShow: 1,
    slidesToScroll: 1
  };

  return (
    <div className="home-page">
      {/* 轮播区域 */}
      <section className="banner-section">
        <div className="banner-wrapper">
          <Slider {...settings}>
            <div>
              <img src={banner1} alt="Banner 1" className="banner-image" />
            </div>
            <div>
              <img src={banner2} alt="Banner 2" className="banner-image" />
            </div>
            <div>
              <img src={banner3} alt="Banner 3" className="banner-image" />
            </div>
          </Slider>
        </div>
      </section>
      
      {/* 内容区域 */}
      <div className="container">
        <section className="section brand-advantage">
          <h2>品牌优势</h2>
          <p>
            我们的品牌以创新、环保和高品质为核心，专注于为客户提供卓越的产品和服务。
            在全球范围内，我们不断推动技术革新和可持续发展。
          </p>
          {/* 可根据需要添加图标、图片或更多说明 */}
        </section>
        <section className="section main-products">
          <h2>主要产品</h2>
          <p>
            我们的产品涵盖高品质色纺纱线、环保再生棉纱等多个系列，满足不同领域的需求，
            助力打造时尚与可持续发展的未来。
          </p>
          {/* 预留产品卡片或网格展示区域 */}
        </section>
      </div>
    </div>
  );
}

export default Home;
