import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "../App.css";

// 占位符图片
const placeholderImage = "https://via.placeholder.com/1200x500?text=Banner+";

function Home() {
  const [carouselImages, setCarouselImages] = useState([]);
  const [homeContent, setHomeContent] = useState([]);

  useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/homepage_carousel/')
      .then(response => setCarouselImages(response.data))
      .catch(error => console.error("Error fetching homepage carousel", error));

    axios.get('http://127.0.0.1:8000/api/homecontent/')
      .then(response => setHomeContent(response.data))
      .catch(error => console.error("Error fetching home content", error));
  }, []);

  const settings = {
    dots: true,
    infinite: true,
    speed: 1000,
    fade: true,
    autoplay: true,
    autoplaySpeed: 3000,
    slidesToShow: 1,
    slidesToScroll: 1,
    arrows: false,
  };

  return (
    <div className="home-page">
      <section className="banner-section">
        <div className="banner-wrapper">
          <Slider {...settings}>
            {carouselImages.length > 0 ? (
              carouselImages.map(item => (
                <div key={item.id}>
                  <img src={`http://127.0.0.1:8000${item.image}`} alt={item.title || "Banner"} className="banner-image" />
                </div>
              ))
            ) : (
              <>
                <div><img src={`${placeholderImage}1`} alt="Banner 1" className="banner-image" /></div>
                <div><img src={`${placeholderImage}2`} alt="Banner 2" className="banner-image" /></div>
                <div><img src={`${placeholderImage}3`} alt="Banner 3" className="banner-image" /></div>
              </>
            )}
          </Slider>
        </div>
      </section>

      <div className="container">
        {homeContent.length > 0 ? (
          homeContent.map(item => (
            <section key={item.id} className="section">
              {item.title && <h2>{item.title}</h2>}
              {item.subtitle && <h3>{item.subtitle}</h3>}
              <p>{item.content}</p>
              {item.image && <img src={`http://127.0.0.1:8000${item.image}`} alt={item.title} />}
            </section>
          ))
        ) : (
          <section className="section">
            <h2>Welcome to Shundao</h2>
            <p>We are a global leader in color spun yarn and sustainable textile solutions.</p>
          </section>
        )}
      </div>
    </div>
  );
}

export default Home;
