import React, { useState, useEffect } from 'react';
import Slider from "react-slick";
import axios from 'axios';
import "../App.css";

// 轮播图图片（可选动态管理，此处暂用静态图片）
import banner1 from "../assets/images/banner1.jpg";
import banner2 from "../assets/images/banner2.jpg";
import banner3 from "../assets/images/banner3.jpg";

function Home() {
  const [homeContent, setHomeContent] = useState([]);

  useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/homecontent/')
      .then(response => setHomeContent(response.data))
      .catch(error => console.error("Error fetching home content", error));
  }, []);

  const settings = {
    dots: true,
    infinite: true,
    speed: 1000,
    fade: true,
    autoplay: true,
    autoplaySpeed: 3000,
    slidesToShow: 1,
    slidesToScroll: 1,
    arrows: false,
  };

  return (
    <div className="home-page">
      <section className="banner-section">
        <div className="banner-wrapper">
          <Slider {...settings}>
            <div>
              <img src={banner1} alt="Banner 1" className="banner-image" />
            </div>
            <div>
              <img src={banner2} alt="Banner 2" className="banner-image" />
            </div>
            <div>
              <img src={banner3} alt="Banner 3" className="banner-image" />
            </div>
          </Slider>
        </div>
      </section>
      <div className="container">
        {homeContent.length > 0 ? (
          homeContent.map(item => (
            <section key={item.id} className="section">
              {item.title && <h2>{item.title}</h2>}
              <p>{item.content}</p>
              {item.image && <img src={`http://127.0.0.1:8000${item.image}`} alt={item.title} />}
            </section>
          ))
        ) : (
          <section className="section">
            <h2>Welcome to Shundao</h2>
            <p>
              We are a global leader in color spun yarn and sustainable textile solutions.
              Our commitment to innovation and environmental responsibility sets us apart.
            </p>
          </section>
        )}
      </div>
    </div>
  );
}

export default Home;
