import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "../App.css";

function Home() {
  const [carouselImages, setCarouselImages] = useState([]);
  const [homeContent, setHomeContent] = useState([]);
  const [brandAdv, setBrandAdv] = useState(null);

  useEffect(() => {
    // 获取轮播图数据
    axios.get('http://127.0.0.1:8000/api/homepage_carousel/')
      .then(response => setCarouselImages(response.data))
      .catch(error => console.error("Error fetching homepage carousel:", error));

    // 获取首页内容（如欢迎语）
    axios.get('http://127.0.0.1:8000/api/homecontent/')
      .then(response => setHomeContent(response.data))
      .catch(error => console.error("Error fetching home content:", error));

    // 获取 Brand Advantage 数据（取第一条）
    axios.get('http://127.0.0.1:8000/api/brand_advantage/')
      .then(response => {
        if (response.data.length > 0) {
          setBrandAdv(response.data[0]);
        }
      })
      .catch(error => console.error("Error fetching brand advantage:", error));
  }, []);

  // 轮播图配置
  const sliderSettings = {
    dots: true,
    infinite: true,
    speed: 1000,
    fade: true,
    autoplay: true,
    autoplaySpeed: 3000,
    slidesToShow: 1,
    slidesToScroll: 1,
    arrows: false,
  };

  return (
    <div className="home-page">
      {/* Banner 轮播图 */}
      <section className="banner-section">
        <div className="banner-wrapper">
          <Slider {...sliderSettings}>
            {carouselImages.length > 0 ? (
              carouselImages.map(item => (
                <div key={item.id}>
                  <img src={item.image} alt={item.title || "Banner"} className="banner-image" />
                </div>
              ))
            ) : (
              <>
                <div><img src="https://via.placeholder.com/1200x500?text=Banner+1" alt="Banner 1" className="banner-image" /></div>
                <div><img src="https://via.placeholder.com/1200x500?text=Banner+2" alt="Banner 2" className="banner-image" /></div>
                <div><img src="https://via.placeholder.com/1200x500?text=Banner+3" alt="Banner 3" className="banner-image" /></div>
              </>
            )}
          </Slider>
        </div>
      </section>

      {/* 首页其他内容 */}
      <div className="container">
        {homeContent.length > 0 ? (
          homeContent.map(item => (
            <section key={item.id} className="section">
              {item.title && <h2>{item.title}</h2>}
              {item.subtitle && <h3>{item.subtitle}</h3>}
              <p>{item.content}</p>
              {item.image && <img src={item.image} alt={item.title} style={{ width: "100%", borderRadius: "8px" }} />}
            </section>
          ))
        ) : (
          <section className="section">
            <h2>Welcome to Shundao</h2>
            <p>
              We are a global leader in color spun yarn and sustainable textile solutions.
            </p>
          </section>
        )}

        {/* Brand Advantage 栏目 */}
        {brandAdv && (
          <section className="section brand-advantage">
            <div className="brand-advantage-container">
              {/* 左侧：堆叠图片 */}
              <div className="brand-advantage-images">
                <div className="layered-images">
                  {brandAdv.image1 && (
                    <img src={brandAdv.image1} alt="Advantage 1" className="layered-image image1" />
                  )}
                  {brandAdv.image2 && (
                    <img src={brandAdv.image2} alt="Advantage 2" className="layered-image image2" />
                  )}
                  {brandAdv.image3 && (
                    <img src={brandAdv.image3} alt="Advantage 3" className="layered-image image3" />
                  )}
                </div>
              </div>
              {/* 右侧：文字内容 */}
              <div className="brand-advantage-text">
                {brandAdv.title && <h2>{brandAdv.title}</h2>}
                <p>{brandAdv.content}</p>
              </div>
            </div>
          </section>
        )}
      </div>
    </div>
  );
}

export default Home;
