import React, { useState, useEffect } from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import './app.css';

const API_BASE_URL = process.env.REACT_APP_API_BASE_URL;

const Home = ({ language }) => {
  const [homeContent, setHomeContent] = useState([]);
  const [carouselImages, setCarouselImages] = useState([]);

  // 轮播设置
  const sliderSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 5000,
  };

  const sliderSettingsSubtopic = {
    ...sliderSettings,
    arrows: false
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [contentRes, carouselRes] = await Promise.all([
          fetch(`${API_BASE_URL}/api/home-content/`),
          fetch(`${API_BASE_URL}/api/homepage-carousel/`)
        ]);
        
        const contentData = await contentRes.json();
        const carouselData = await carouselRes.json();
        
        setHomeContent(contentData);
        setCarouselImages(carouselData);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="home-page">
      {/* 首页轮播图 */}
      <div className="main-carousel">
        <Slider {...sliderSettings}>
          {carouselImages.map((item) => (
            <div key={item.id} className="carousel-item">
              <img
                src={`${API_BASE_URL}${item.image}`}
                alt={item.title}
                className="carousel-image"
              />
              <div className="carousel-caption">
                <h2>{item.title}</h2>
              </div>
            </div>
          ))}
        </Slider>
      </div>

      {/* 首页正文内容 */}
      <div className="container">
        {homeContent.length > 0 ? (
          homeContent.map(item => (
            <section key={item.id} className="content-section">
              <h2 className="section-title">
                {language === "en" ? item.title_en : item.title_vi}
              </h2>
              
              {item.subtitle_en && (
                <h3 className="section-subtitle">
                  {language === "en" ? item.subtitle_en : item.subtitle_vi}
                </h3>
              )}

              <div className="content-text">
                {language === "en" ? (
                  <FormattedText content={item.content_en} />
                ) : (
                  <FormattedText content={item.content_vi} />
                )}
              </div>

              {item.image && (
                <img 
                  src={`${API_BASE_URL}${item.image}`}
                  alt={language === "en" ? item.title_en : item.title_vi}
                  className="section-image"
                />
              )}

              {item.subtopics?.length > 0 && (
                <div className="subtopics-container">
                  {item.subtopics.map(subtopic => (
                    <SubtopicBlock 
                      key={subtopic.id}
                      subtopic={subtopic}
                      language={language}
                      sliderSettings={sliderSettingsSubtopic}
                    />
                  ))}
                </div>
              )}
            </section>
          ))
        ) : (
          <p>Loading content...</p>
        )}
      </div>
    </div>
  );
};

// 子组件：格式化文本内容
const FormattedText = ({ content }) => (
  <p className="preserve-newlines">{content}</p>
);

// 子组件：子主题区块
const SubtopicBlock = ({ subtopic, language, sliderSettings }) => {
  const layoutClass = `layout-${subtopic.layout || 'vertical'}`;
  const textAlignClass = `text-${subtopic.text_position || 'center'}`;
  const spacingValue = subtopic.image_spacing ? `${subtopic.image_spacing}px` : '16px';

  return (
    <div 
      className={`subtopic-block ${layoutClass} ${textAlignClass}`}
      style={{ '--spacing': spacingValue }}
    >
      <div className="subtopic-content">
        <h3 className="subtopic-title">
          {language === "en" ? subtopic.title_en : subtopic.title_vi}
        </h3>
        <FormattedText 
          content={language === "en" ? subtopic.content_en : subtopic.content_vi} 
        />
      </div>

      {subtopic.images?.length > 0 && (
        <div className="subtopic-media">
          {subtopic.layout === 'carousel' ? (
            <Slider {...sliderSettings} className="subtopic-carousel">
              {subtopic.images.map(img => (
                <CarouselImage key={img.id} img={img} />
              ))}
            </Slider>
          ) : (
            <div className="image-grid">
              {subtopic.images.map(img => (
                <GridImage key={img.id} img={img} />
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

// 子组件：轮播图片
const CarouselImage = ({ img }) => (
  <div className="carousel-image-container">
    <img
      src={`${API_BASE_URL}${img.image_url}`}
      alt={img.alt_text || "Subtopic image"}
      style={{
        width: img.custom_width || '100%',
        height: img.custom_height || 'auto',
      }}
    />
  </div>
);

// 子组件：网格图片
const GridImage = ({ img }) => (
  <div className="grid-image-wrapper">
    <img
      src={`${API_BASE_URL}${img.image_url}`}
      alt={img.alt_text || "Subtopic image"}
      style={{
        width: img.custom_width || '100%',
        height: img.custom_height || 'auto',
      }}
    />
  </div>
);

export default Home;
