import React, { useEffect, useState } from 'react';

function Homepage() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch('http://localhost:8000/api/products/')
      .then(response => response.json())
      .then(data => setProducts(data))
      .catch(error => console.error('Error fetching products:', error));
  }, []);

  return (
    <div>
      {/* 动态Banner部分 */}
      <div style={{ backgroundColor: '#eee', padding: '20px', textAlign: 'center' }}>
        <h2>Welcome to Shundao</h2>
        <p>Ecology, Innovation, Sustainability</p>
      </div>

      {/* 品牌优势部分 */}
      <section style={{ padding: '20px' }}>
        <h2>Brand Advantage</h2>
        <ul>
          <li>独立研发中心</li>
          <li>太阳能屋顶清洁能源</li>
          <li>稳定卓越的产品品质</li>
        </ul>
      </section>

      {/* 主要产品部分 */}
      <section style={{ padding: '20px' }}>
        <h2>Main Products</h2>
        <ul>
          {products.map(product => (
            <li key={product.id}>
              <h3>{product.name}</h3>
              <p>{product.description}</p>
              {product.image && <img src={`http://localhost:8000${product.image}`} alt={product.name} width="200" />}
            </li>
          ))}
        </ul>
      </section>
    </div>
  );
}

export default Homepage;
