import React from 'react';
import Slider from "react-slick";
import "../App.css";
// 轮播图图片（请确保图片存在于 src/assets/images/ 下）
import banner1 from "../assets/images/banner1.jpg";
import banner2 from "../assets/images/banner2.jpg";
import banner3 from "../assets/images/banner3.jpg";
// 品牌优势叠层效果图片
import advantage1 from "../assets/images/advantage1.jpg";
import advantage2 from "../assets/images/advantage2.jpg";
import advantage3 from "../assets/images/advantage3.jpg";

function Home() {
  // 轮播图设置，使用淡入淡出效果
  const settings = {
    dots: true,
    infinite: true,
    speed: 1000,
    fade: true,
    autoplay: true,
    autoplaySpeed: 3000,
    slidesToShow: 1,
    slidesToScroll: 1,
    arrows: false,
  };

  return (
    <div className="home-page">
      {/* 轮播图区域 */}
      <section className="banner-section">
        <div className="banner-wrapper">
          <Slider {...settings}>
            <div>
              <img src={banner1} alt="Banner 1" className="banner-image" />
            </div>
            <div>
              <img src={banner2} alt="Banner 2" className="banner-image" />
            </div>
            <div>
              <img src={banner3} alt="Banner 3" className="banner-image" />
            </div>
          </Slider>
        </div>
      </section>

      {/* 页面内容区域 */}
      <div className="container">
        <section className="section business-intro">
          <h2>Welcome to Shundao</h2>
          <p>
            We are a global leader in color spun yarn and sustainable textile solutions.
            Our commitment to innovation and environmental responsibility sets us apart.
          </p>
        </section>

        {/* 品牌优势区：左右布局，左侧文字，右侧叠层图片 */}
        <section className="section brand-advantage">
          <div className="brand-advantage-container">
            <div className="brand-advantage-text">
              <h2>Brand Advantage</h2>
              <p>
                Our brand stands for cutting-edge technology, sustainability, and exceptional quality.
                We continuously invest in innovative solutions to stay ahead in the industry.
              </p>
            </div>
            <div className="brand-advantage-images">
              <div className="layered-images">
                <img src={advantage1} alt="Advantage 1" className="layered-image image1" />
                <img src={advantage2} alt="Advantage 2" className="layered-image image2" />
                <img src={advantage3} alt="Advantage 3" className="layered-image image3" />
              </div>
            </div>
          </div>
        </section>

        <section className="section main-products">
          <h2>Main Products</h2>
          <p>
            Our product range includes premium color spun yarns, eco-friendly recycled cotton yarns, and more,
            designed to meet modern demands for fashion and sustainability.
          </p>
          {/* 预留产品展示区域 */}
        </section>
      </div>
    </div>
  );
}

export default Home;
