import React, { useEffect, useState } from 'react';
import '../App.css';

function ProductsCenter() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch('http://localhost:8000/api/products/')
      .then(response => response.json())
      .then(data => setProducts(data))
      .catch(error => console.error('Error fetching products:', error));
  }, []);

  return (
    <div className="container" style={{ padding: '20px' }}>
      <h1>Products Center</h1>
      <div className="section">
        <p>Explore our range of yarns:</p>
        <ul>
          {products.map(product => (
            <li key={product.id} style={{ marginBottom: '20px' }}>
              <h3>{product.name}</h3>
              <p>{product.description}</p>
              {product.image && <img src={`http://localhost:8000${product.image}`} alt={product.name} style={{ maxWidth: '100%', borderRadius: '8px' }} />}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default ProductsCenter;
