import React from 'react';
import '../App.css';

function Home() {
  return (
    <div className="home-page">
      <section className="hero-section">
        <div className="hero-content">
          <h1>Shundao</h1>
          <p>Innovation. Sustainability. Excellence.</p>
          <a href="/about-us" className="cta-button">Learn More</a>
        </div>
        <div className="hero-image">
          {/* 预留横幅图片区域，请替换 hero-banner.jpg 为你自己的图片 */}
          <img src="./assets/images/hero-banner.jpg" alt="Hero Banner" />
        </div>
      </section>
      <div className="container">
        <section className="section business-intro">
          <h2>Welcome to Shundao</h2>
          <p>
            We are a global leader in color spun yarn and sustainable textile solutions.
            Our advanced technology and commitment to environmental responsibility set us apart.
          </p>
        </section>
        {/* 可根据需要继续添加其他内容版块 */}
      </div>
    </div>
  );
}

export default Home;
